'use strict';

var objutil = require('objutil'),
    Activity = require('./activity'),
    MessageClass = require('../constants').MessageClass;


function Heartbeat() {
    Heartbeat.super_.apply(this, arguments);
    this.messageClass = MessageClass.HEARTBEAT;
    this._bufferable = false;
}

objutil.extend(Heartbeat, Activity);


exports = module.exports = Heartbeat;
