'use strict';

var loadHeader = require('global-header-ebay').loadHeader;
var loadFooter = require('global-header-ebay').loadFooter;
var getRequest = require('marko-context-ebay').getRequest;

module.exports = function globalFooterTag(input, out) {

    var req = getRequest(out);
    var headerAsyncValue = loadHeader(req, input);
    var footerAsyncValue = loadFooter(req, input);

    out.global.globalHeader = {
        options: input,
        header: headerAsyncValue,
        footer: footerAsyncValue
    };
};