'use strict';

var Assert = require('assert');
var Utils = require('../lib/utils');

describe(__filename, function () {
    it('it should sanitize the object', function () {
        Assert.deepEqual({
            foo: 'bar',
            exu: 'zeo',
            authorization: '******',
            Authorization: '******'
        }, Utils.sanitize({
            foo: 'bar',
            exu: 'zeo',
            authorization: 'Bear 631863131kjdhqkjdhkd',
            Authorization: 'Bear 631863131kjdhqkjdhkd',
        }));

        Assert.deepEqual({}, Utils.sanitize({}));
        Assert.deepEqual(undefined, Utils.sanitize(undefined));
        Assert.deepEqual('', Utils.sanitize(''));
        Assert.deepEqual('test', Utils.sanitize('test'));
        Assert.deepEqual('test', Utils.sanitize(new Buffer('test')).toString());
        Assert.deepEqual(null, Utils.sanitize(null));
        Assert.deepEqual([], Utils.sanitize([]));
        Assert.deepEqual(['test'], Utils.sanitize(['test']));
        Assert.deepEqual(1, Utils.sanitize(1));
        Assert.deepEqual(0, Utils.sanitize(0));
        Assert.deepEqual(false, Utils.sanitize(false));
        Assert.deepEqual(true, Utils.sanitize(true));

    });
});
