'use strict';

function PercentileBucket(windowStart) {
    this.windowStart = windowStart;
    this.bucketValues = [];

}

PercentileBucket.prototype = {
    addValue: function (value) {
        value = value || 0;
        this.bucketValues.push(value);
    },

    get values() {
        return this.bucketValues;
    }
};

module.exports = PercentileBucket;
