'use strict';

var Utils = require('./utils');

var appConfigRoot = process.argv[2];
var moduleConfigRoot = process.argv[3];

Utils.loadModuleConfig({
    appConfigRoot: appConfigRoot,
    moduleConfigRoot: moduleConfigRoot
}, function (err, config) {
    config = config && config._store || {};
    config = JSON.stringify(config);
    process.stdout.write(Utils.CONFIG_TOKEN + config);
    process.exit(0);
});
