'use strict';

module.exports = {

    getBaseHost: function (hostname) {
        var ebayHost = hostname || '.ebay.com';

        var matches = ebayHost.match(/(([^\.]+\.paradise\.qa\.ebay|qa\.ebay|latest\.ebay|ebay)\.[^\/:]+)/);
        if (matches) {
            ebayHost = matches[0];
        }
        return ebayHost;
    },
    getPageNotRespondingUrl: function (hostname) {
        return 'http://pages.' + this.getBaseHost(hostname) + '/messages/page_not_responding.html';

    }
};