'use strict';
var assert = require('assert');
var httpUtils = require('../index');

describe(__filename, function () {
    it('Should work ', function (done) {
        var basehost = httpUtils.getBaseHost('www.ebay.com');
        assert.ok(basehost);
        assert.equal(basehost, 'ebay.com');


        basehost = httpUtils.getBaseHost('www.ebay.de');
        assert.ok(basehost);
        assert.equal(basehost, 'ebay.de');

        basehost = httpUtils.getBaseHost('www.ebay.paradise.co.uk');
        assert.ok(basehost);
        assert.equal(basehost, 'ebay.paradise.co.uk');

        var url = httpUtils.getPageNotRespondingUrl('www.ebay.paradise.co.uk');
        assert.ok(url);
        assert.equal(url,'http://pages.ebay.paradise.co.uk/messages/page_not_responding.html');

        url = httpUtils.getPageNotRespondingUrl('www.ebay.com');
        assert.ok(url);
        assert.equal(url,'http://pages.ebay.com/messages/page_not_responding.html');

        done();
    });
});
