$(document).ready(function() {
 if (typeof initPage == 'function') { initPage();}	  
 var $table = $("#categoryListTableId");
 var oTable = undefined;
 if ($table) {
  $table.addClass("display");
  oTable = $table.dataTable({
   "bJQueryUI": true,
   "bProcessing" : true,
   "bPaginate": false,
   "bScrollCollapse": true, 
   "bServerSide": false
  });
 
  $d = $("#categoryListTableId_filter");   
  if ($d && $("#categoryListTableId_title")) {
   	$d.before($("#categoryListTableId_title"));
  }
  
  $(window).bind('resize', function () {
  moreTopMenu();
  if (!oTable) return false;
  oTable.fnAdjustColumnSizing(true);
  return true;
  });
 }
 initTopMenu();
});
