'use strict';

var XmlDom = require('xmldom');
var XPath = require('xpath');

module.exports.select = function select(xml, xpath) {
	var Dom = XmlDom.DOMParser;

	var doc = new Dom().parseFromString(xml);

    var res = '<xpath-result>' +
        (XPath.select(xpath, doc).toString() || '') +
        '</xpath-result>';

    return res;
};
